@import 'scss/variables.scss';

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    font-size: 16px;

    @include container {
        font-size: 14px;
    }
}

body {
    font-family: 'Open Sans', sans-serif;
}
.container {
    max-width: 1132px;
    display: block;
    margin: auto;

    @include container {
        padding-left: 25px;
        padding-right: 25px;
    }

    &_big {
        max-width: 1261px;
    }
}

@import 'scss/textClasses.scss';
@import 'scss/button.scss';
@import 'scss/header.scss';
@import 'scss/nav.scss';
@import 'scss/menu.scss';
@import 'scss/contacts.scss';
@import 'scss/footer.scss';
@import 'scss/subscribe.scss';
@import 'scss/welcome.scss';
@import 'scss/advantages.scss';
@import 'scss/about.scss';
@import 'scss/catalog.scss';

sources.scss\\variables.scss:
$base-green: #476200;
$soft-black: #1C1C1C;

//--------DEVICE SIZE--------
$small-mobile: 400px;
$tablet-width: 648px;
$desktop-width: 1023px;
$container-width: 1300px;

@mixin small-mobile {
  @media (max-width: $small-mobile) {
    @content;
  }
}

@mixin mobile {
  @media (max-width: #{$tablet-width - 1px}) {
    @content;
  }
}

@mixin tablet {
  @media (max-width: #{$desktop-width}) {
    @content;
  }
}


@mixin desktop {
  @media (min-width: #{$desktop-width + 1px}) {
    @content;
  }
}

@mixin container {
  @media (min-width: #{$desktop-width}) and (max-width: #{$container-width}) {
    @content;
  }
}

