.welcome {
    position: relative;
    padding-bottom: 19px;

    &:after {
        content: '';
        display: block;
        position: absolute;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        background: linear-gradient(0deg, rgba(0, 0, 0, 0.65), rgba(0, 0, 0, 0.65));
    }

    &__wrap {
        position: relative;
        z-index: 1;
        padding-top: 52px;
    }

    &__title {
        font-weight: 300;
        font-size: 2.1875rem;
        line-height: 3rem;
        text-transform: uppercase;
        color: #FFFFFF;
        text-align: center;
    }

    &__title-big {
        font-weight: bold;
        font-size: 2.8125rem;
        line-height: 3.8125rem;
        position: relative;

        &:after {
            content: '*';
            position: absolute;
            right: -0.7em;
            top: -0.4em;
        }
    }

    &__separator {
        display: flex;
        justify-content: space-between;
        margin-top: 26px;

        div {
            flex: auto;
            background-color: $base-green;
            height: 30px;
        }

        &::after,
        &::before {
            content: '';
            border-top: 30px solid #476200;
        }

        &::after {
            border-right: 30px solid transparent;
        }

        &::before {
            border-left: 30px solid transparent;
        }
    }

    &__subtitle {
        font-weight: 300;
        font-size: 2.1875rem;
        line-height: 3rem;
        text-transform: uppercase;
        color: #FFFFFF;
        margin-top: 28px;
        text-align: center;
    }

    &__actions {
        margin-top: 32px;
        text-align: center;
    }

    &__btn {
        font-size: 1.875rem;
        line-height: 2.5625rem;
        padding: 9px 22px;
        min-width: 339px;
    }

    &__catalog {
        margin-right: 16px;
    }

    &__footer {
        font-style: italic;
        font-weight: normal;
        font-size: 1.25rem;
        line-height: 1.6875rem;
        color: #FFFFFF;
        display: flex;
        justify-content: space-between;
        position: relative;
        margin-top: 59px;

    }

    &__footer-txt {
        &:before {
            content: "*";
            margin-right: 0.1em;
            position: relative;
            top: -0.4em;
            ;
        }
    }

    &__anime {
        display: flex;
        flex-direction: column;
        position: absolute;
        left: calc(50% - 17.5px);
        bottom: 0;
        cursor: pointer;
    }

    &__anime-arrow {
        animation: arrow 8s ease-in-out infinite;

        &:first-child {
            position: relative;
            top: 8px;
        }

        &:last-child {
            animation: arrow2 8s ease-in-out infinite;
        }
    }
}


@keyframes arrow {
    0% {
        transform: translateY(0);
    }

    7% {
        transform: translateY(-20px);
        opacity: 1;
    }

    10% {
        opacity: 0;
        transform: translateY(20px);
    }

    15%,
    100% {
        transform: translateY(0);
    }
    
    20% {
        opacity: 0;
    }
    
    25% {
        opacity: 1;
    }
}

@keyframes arrow2 {

    0%,
    9% {
        transform: translateY(0);
        opacity: 1;
    }

    15% {
        transform: translateY(20px);
        opacity: 0;
    }

    16% {
        transform: translateY(0);
    }

    30%,
    100% {
        opacity: 1;
    }
}

