sources.scss\\about.scss:
.about {
    margin-top: 102px;

    &__grid {
        margin-top: 55px;
        display: grid;
        grid-gap: 32px 30px;
        grid-template-columns: 1fr 1fr 1fr 1fr;
        grid-template-areas: 'n1 n2 n2 n3'
            'n4 n5 n6 n6';
    }

    &__grid-item {
        height: 154px;
        position: relative;
        border-radius: 3px;
        overflow: hidden;
        color: #fff;
        display: flex;
        align-items: flex-end;
        padding: 16px 24px;
        text-decoration: none;

        &:after {
            content: '';
            position: absolute;
            width: 100%;
            height: 100%;
            left: 0;
            top: 0;
            z-index: 1;
            background: linear-gradient(0deg, rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.3));
        }

        @for $i from 1 through 6 {
            &:nth-child(#{$i}) {
                grid-area: n#{$i};
            }
        }

        span {
            position: relative;
            z-index: 2;
            font-weight: bold;
            font-size: 15px;
            line-height: 19px;
            letter-spacing: 1.04px;
            text-transform: uppercase;
        }

        &:hover {
            .about__grid-bg {
                transform: scale(1.06);
            }
        }
    }

    &__grid-bg {
        position: absolute;
        width: 100%;
        height: 100%;
        left: 0;
        top: 0;
        object-fit: cover;
        object-position: center;
        transition: all cubic-bezier(0.4, 0, 0.2, 1) .4s;
        transform-origin: center;
    }
}

sources.scss\\catalog.scss:
.catalog {
    margin-top: 102px;

    &__grid {
        display: grid;
        grid-template-rows: 1fr 1fr;
        grid-template-columns: 1fr 1fr;
        position: relative;
        margin-top: 68px;

        &:before,
        &:after {
            content: '';
            background-color: #F6F6F6;
            position: absolute;
        }

        &:before {
            top: calc(50% - 0.5px);
            left: 0;
            height: 1px;
            width: 100%;
        }

        &:after {
            left: calc(50% - 0.5px);
            top: 0;
            width: 1px;
            height: 100%;
        }
    }

    &__item {
        display: flex;
        text-decoration: none;
        font-size: 14px;
        line-height: 19px;
        color: #424242;
        padding: 3px 60px 20px 0;

        &:hover {
            .catalog__item-title:after {
                width: 100%;
            }
        }
    }

    &__item-img {
        width: 170px;
        height: 170px;
        margin-right: 5px;
        object-fit: contain;
        object-position: center;
    }

    &__item-text {
        padding-top: 25px;
    }

    &__item-title {
        font-weight: normal;
        font-size: 16px;
        line-height: 22px;
        color: #212121;
        padding-bottom: 8px;
        position: relative;

        &:after {
            content: '';
            display: block;
            position: absolute;
            left: 0;
            bottom: -1px;
            height: 1px;
            width: 33%;
            background-color: #E9E9E9;
            transition: all ease-out .2s;
        }
    }

    &__item-desc {
        margin-top: 8px;
    }

}
